import React, { useEffect, useRef } from 'react';
import './HeroSection.css';
import heroBg from '../save.jpg';
import sideImg from '../2side.jpg';

const HeroSection = () => {
    const heroRef = useRef(null);

    useEffect(() => {
        const applyHeroSizing = () => {
            const header = document.querySelector('.header');
            const headerH = header ? header.offsetHeight : 80;
            // compute the exact hero height in pixels to avoid timing issues
            const heroH = Math.max(200, window.innerHeight - headerH);

            // expose for legacy CSS that may read the var
            document.documentElement.style.setProperty('--header-height', `${headerH}px`);

            try {
                const heroEl = heroRef.current;
                if (heroEl) {
                    // Set explicit pixel height on the hero container so the
                    // image sizing doesn't depend on CSS variables or timing.
                    heroEl.style.height = `${heroH}px`;

                    const img = heroEl.querySelector('.hero-fullimg');
                    if (img) {
                        img.style.maxHeight = `${heroH}px`;
                        img.style.width = 'auto';
                        // Force a reflow so the browser applies the new sizing
                        // immediately. This helps prevent transient cropping when
                        // returning to the hero after scrolling.
                        // eslint-disable-next-line no-unused-expressions
                        img.getBoundingClientRect();
                    }
                }
            } catch (e) {
                // ignore any DOM timing errors
            }
        };

        applyHeroSizing();
        window.addEventListener('resize', applyHeroSizing);

        // Ensure the hero image sizing is re-applied when the hero enters view
        const heroEl = heroRef.current;
        if (heroEl && 'IntersectionObserver' in window) {
            const io = new IntersectionObserver((entries) => {
                entries.forEach((entry) => {
                    const header = document.querySelector('.header');
                    const headerH = header ? header.offsetHeight : 80;

                    if (entry.isIntersecting) {
                        heroEl.classList.add('hero-in-view');
                        // re-apply sizing in case something changed while
                        // the hero was offscreen
                        applyHeroSizing();

                        // If the hero's top is tucked under the sticky header
                        // (user scrolled back and header is overlaying the hero),
                        // scroll so the hero sits just below the header. This
                        // prevents the half-visible/cropped appearance on return.
                        try {
                            const rect = entry.boundingClientRect;
                            // rect.top is relative to viewport; if it's less than
                            // header height the header overlaps the hero.
                            if (rect.top < headerH) {
                                const docTop = window.scrollY + rect.top;
                                const target = Math.max(0, docTop - headerH);
                                // Use instant reposition to avoid visible flicker.
                                window.scrollTo({ top: Math.round(target), behavior: 'auto' });
                            }
                        } catch (e) {
                            // ignore any scroll errors
                        }
                    } else {
                        heroEl.classList.remove('hero-in-view');
                    }
                });
            }, { threshold: 0.1 });
            io.observe(heroEl);
            return () => {
                io.disconnect();
                window.removeEventListener('resize', applyHeroSizing);
            };
        }

        return () => window.removeEventListener('resize', applyHeroSizing);
    }, []);

    return (
        <section className="hero" ref={heroRef} role="img" aria-label="Hero background image">
            {/* Left side decorative image */}
            <div className="hero-side hero-side-left">
                <img src={sideImg} alt="" />
            </div>

            <div className="hero-bg">
                <img className="hero-fullimg" src={heroBg} alt="Hero background" />
            </div>

            {/* Right side decorative image */}
            <div className="hero-side hero-side-right">
                <img src={sideImg} alt="" />
            </div>

            <div className="hero-copy">
                <div className="hero-copy-inner">
                    <h1>Art speaks where words fall short</h1>
                    <p>Original ideas, colour and form â€” a canvas holds memory and meaning.</p>
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
