];

const categories = ["All", "Nature Art", "Animal Art", "Landscape", "Abstract", "Artist Feature", "Conceptual", "Cultural", "Modern Art", "Indian Art", "Home Decor", "Fine Art", "Art History"];

const BlogSection = () => {
    const [activeCategory, setActiveCategory] = useState("All");
    const [visibleCount, setVisibleCount] = useState(9);
    const sectionRef = useRef(null);
    const snapContainerRef = useRef(null);
    const activeIndexRef = useRef(0);
    const [activeIndex, setActiveIndex] = useState(0);
    const touchStartRef = useRef({ x: 0, y: 0 });
    const scrollLockRef = useRef(false);
    const pinSpacerRef = useRef(null);
    const pinInnerRef = useRef(null);

    // Intersection Observer for scroll animations
    useEffect(() => {
        // Set a CSS variable with the current header height so the snap container
        // can size itself to the viewport minus the header. This keeps the
        // snapping sections full-screen below the sticky header.
        const setHeaderHeightVar = () => {
            const header = document.querySelector('.header');
            const h = header ? header.offsetHeight : 80;
            document.documentElement.style.setProperty('--header-height', `${h}px`);
        };

        setHeaderHeightVar();
        window.addEventListener('resize', setHeaderHeightVar);
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        entry.target.classList.add('visible');
                    }
                });
            },
            {
                threshold: 0.1,
                rootMargin: '0px 0px -50px 0px'
            }
        );

    // Observe non-pinned animated elements only. Pinned panels are
    // controlled by the activeIndex state to ensure only one is visible.
    const allAnimated = Array.from(document.querySelectorAll('.scroll-animate'));
    const animatedElements = allAnimated.filter(el => !el.closest('.pin-inner'));
    animatedElements.forEach((el) => observer.observe(el));

        return () => {
            animatedElements.forEach((el) => observer.unobserve(el));
            window.removeEventListener('resize', setHeaderHeightVar);
        };
    }, [visibleCount]);

    // ...existing code...

    // Helper to get current visible snap items
    const getSnapItems = () => {
        const container = snapContainerRef.current;
        if (!container) return [];
        return Array.from(container.querySelectorAll('.snap-item'));
    };

    const clampIndex = (i, len) => Math.max(0, Math.min(i, Math.max(0, len - 1)));

    const goToIndex = (i) => {
        const items = getSnapItems();
        if (!items.length) return;
        const idx = clampIndex(i, items.length);
        if (scrollLockRef.current || idx === activeIndexRef.current) return;
        scrollLockRef.current = true;
        activeIndexRef.current = idx;
        setActiveIndex(idx);
        // If pin spacer is present we compute the scroll position to land on the panel
        if (pinSpacerRef.current) {
            const headerH = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--header-height')) || 80;
            const panelH = window.innerHeight - headerH;
            const start = pinSpacerRef.current.offsetTop;
            const target = start + (idx * panelH);
            window.scrollTo({ top: target, behavior: 'smooth' });
        } else {
            // Fallback: Use smooth scroll to bring the snap item under the header
            items[idx].scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
        // unlock after animation completes (approx)
        setTimeout(() => { scrollLockRef.current = false; }, 700);
    };

    // Wheel (mouse) handler - supports vertical and horizontal swipes
    const onWheel = (e) => {
        // Prevent default page scrolling to take full control
        e.preventDefault();
        if (scrollLockRef.current) return;
        const deltaX = e.deltaX;
        const deltaY = e.deltaY;
        // Ignore tiny scrolls
        const thresh = 25;
        let moved = false;
        // If we have a spacer, allow exit when at the ends
        const spacer = pinSpacerRef.current;
        const items = getSnapItems();
        const lastIdx = items.length - 1;

        if (Math.abs(deltaY) > Math.abs(deltaX)) {
            if (deltaY > thresh) {
                // scrolling down
                if (spacer && activeIndexRef.current === lastIdx) {
                    // scroll past the spacer to reveal load-more/footer
                    const headerH = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--header-height')) || 80;
                    const start = spacer.offsetTop;
                    const target = start + spacer.offsetHeight - headerH + 10;
                    scrollLockRef.current = true;
                    window.scrollTo({ top: target, behavior: 'smooth' });
                    setTimeout(() => { scrollLockRef.current = false; }, 800);
                    moved = true;
                } else {
                    goToIndex(activeIndexRef.current + 1);
                    moved = true;
                }
            } else if (deltaY < -thresh) {
                // scrolling up
                if (spacer && activeIndexRef.current === 0) {
                    const headerH = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--header-height')) || 80;
                    const start = spacer.offsetTop;
                    const target = Math.max(0, start - headerH - 10);
                    scrollLockRef.current = true;
                    window.scrollTo({ top: target, behavior: 'smooth' });
                    setTimeout(() => { scrollLockRef.current = false; }, 800);
                    moved = true;
                } else {
                    goToIndex(activeIndexRef.current - 1);
                    moved = true;
                }
            }
        } else {
            if (deltaX > thresh) { goToIndex(activeIndexRef.current + 1); moved = true; }
            else if (deltaX < -thresh) { goToIndex(activeIndexRef.current - 1); moved = true; }
        }
        // If we moved, set a short lock so multiple wheel events don't skip panels
        if (moved) {
            scrollLockRef.current = true;
            setTimeout(() => { scrollLockRef.current = false; }, 650);
        }
    };

    // Touch handlers for mobile swipe
    const onTouchStart = (e) => {
        const t = e.touches[0];
        touchStartRef.current = { x: t.clientX, y: t.clientY };
    };

    const onTouchEnd = (e) => {
        if (scrollLockRef.current) return;
        const items = getSnapItems();
        if (!items.length) return;
        const last = e.changedTouches ? e.changedTouches[0] : null;
        if (!last) return;
        const dx = last.clientX - touchStartRef.current.x;
        const dy = last.clientY - touchStartRef.current.y;
        const absX = Math.abs(dx);
        const absY = Math.abs(dy);
        const swipeThresh = 50;
        if (absY > absX && absY > swipeThresh) {
            if (dy < 0) goToIndex(activeIndexRef.current + 1);
            else goToIndex(activeIndexRef.current - 1);
        } else if (absX > absY && absX > swipeThresh) {
            if (dx < 0) goToIndex(activeIndexRef.current + 1);
            else goToIndex(activeIndexRef.current - 1);
        }
        // lock briefly after a swipe to avoid accidental multi-advances
        scrollLockRef.current = true;
        setTimeout(() => { scrollLockRef.current = false; }, 650);
    };

    // Keyboard navigation
    useEffect(() => {
        const onKey = (e) => {
            if (scrollLockRef.current) return;
            if (['ArrowDown', 'PageDown', 'ArrowRight'].includes(e.key)) {
                e.preventDefault();
                goToIndex(activeIndexRef.current + 1);
            } else if (['ArrowUp', 'PageUp', 'ArrowLeft'].includes(e.key)) {
                e.preventDefault();
                goToIndex(activeIndexRef.current - 1);
            } else if (e.key === 'Home') {
                e.preventDefault();
                goToIndex(0);
            } else if (e.key === 'End') {
                e.preventDefault();
                const items = getSnapItems();
                goToIndex(items.length - 1);
            }
        };
        window.addEventListener('keydown', onKey, { passive: false });
        return () => window.removeEventListener('keydown', onKey);
    }, []);

    const filteredBlogs = activeCategory === "All" 
        ? blogData 
        : blogData.filter(blog => blog.category === activeCategory);

    const visibleBlogs = filteredBlogs.slice(0, visibleCount);

    // Ensure only the active pinned snap-item has the 'visible' class so
    // its children animate; others remain hidden while pinned.
    useEffect(() => {
        const items = getSnapItems();
        items.forEach((item, idx) => {
            if (idx === activeIndex) item.classList.add('visible');
            else item.classList.remove('visible');
        });
    }, [activeIndex, visibleBlogs.length]);

    const handleLoadMore = () => {
        setVisibleCount(prev => prev + 6);
    };

    const handleCategoryChange = (category) => {
        setActiveCategory(category);
        setVisibleCount(9);
    };

    // Scroll mapping for pinned panels (maps scroll position to activeIndex)
    useEffect(() => {
        const onScroll = () => {
            // Don't react to scroll events while a programmatic scroll is in progress
            if (scrollLockRef.current) return;

            const spacer = pinSpacerRef.current;
            if (!spacer) return;
            const rect = spacer.getBoundingClientRect();
            const headerH = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--header-height')) || 80;
            const start = rect.top + window.scrollY; // top of spacer relative to document
            const panelH = window.innerHeight - headerH;
            const scrollY = window.scrollY;
            // Only map when spacer is in or near viewport
            if (scrollY >= start - panelH && scrollY <= start + spacer.offsetHeight - headerH) {
                const rel = scrollY - start;
                // compute index by rounding to nearest panel
                const idx = clampIndex(Math.round(rel / panelH), visibleBlogs.length);
                if (idx !== activeIndexRef.current) {
                    activeIndexRef.current = idx;
                    setActiveIndex(idx);
                }
            }
        };

        window.addEventListener('scroll', onScroll, { passive: true });
        // run once to pick correct index on load
        onScroll();
        return () => window.removeEventListener('scroll', onScroll);
    }, [visibleBlogs.length]);

    // Add a non-passive global wheel listener so we can reliably prevent
    // browser default momentum scrolling when the pinned spacer is active.
    useEffect(() => {
        const wheelHandler = (ev) => {
            const spacer = pinSpacerRef.current;
            if (!spacer) return;
            const headerH = parseInt(getComputedStyle(document.documentElement).getPropertyValue('--header-height')) || 80;
            const start = spacer.offsetTop;
            const panelH = window.innerHeight - headerH;
            const scrollY = window.scrollY;
            // Only intercept while the spacer area is in view
            if (scrollY >= start - panelH && scrollY <= start + spacer.offsetHeight - headerH) {
                // forward the native wheel event to our onWheel handler and prevent default
                try { ev.preventDefault(); } catch (e) {}
                // adapt the native wheel event to the shape expected by onWheel
                onWheel(ev);
            }
        };

        window.addEventListener('wheel', wheelHandler, { passive: false });
        return () => window.removeEventListener('wheel', wheelHandler, { passive: false });
    }, [visibleBlogs.length]);

    return (
        <section className="blog-section" ref={sectionRef}>
            <div className="blog-container">
                <div className="section-header scroll-animate">
                    <span className="section-tag">Our Stories</span>
                    <h2 className="section-title">Latest from the Blog</h2>
                    <p className="section-desc">
                        Explore the world of art through our curated collection of articles, 
                        artist features, and insights into the creative process.
                    </p>
                </div>

                <div className="fresh-heading scroll-animate scroll-animate-delay-1">FRESH POSTS</div>

                {/* Snap container: each article becomes a full viewport panel that
                    snaps vertically. We keep the scroll-animate wrapper on each
                    snap item so existing reveal animations still run. */}
                <div className="blog-list">
                    {/* Pin spacer creates the scrollable area length */}
                    <div className="pin-spacer" ref={pinSpacerRef} style={{ height: `${visibleBlogs.length * 100}vh` }}>
                        <div className="pin-inner" ref={pinInnerRef} onWheel={onWheel} onTouchStart={onTouchStart} onTouchEnd={onTouchEnd} role="list" aria-label="Article panels">
                            <div className="snap-container" ref={snapContainerRef}>
                                {visibleBlogs.map((blog, index) => (
                                    <section
                                        className={`snap-item scroll-animate ${activeIndex === index ? 'active' : ''}`}
                                        style={{ ['--i']: index % 6, transitionDelay: `${(index % 3) * 0.12}s` }}
                                        key={blog.id}
                                        data-index={index}
                                        tabIndex={-1}
                                        role="listitem"
                                        aria-current={activeIndex === index}
                                    >
                                        <BlogListItem blog={blog} reverse={index % 2 === 1} featured={index === 0} />
                                    </section>
                                ))}
                            </div>
                        </div>
                    </div>

                    {/* Simple indicator dots for current panel */}
                    <div className="snap-indicator" aria-hidden>
                        {visibleBlogs.map((b, i) => (
                            <button
                                key={b.id}
                                className={`dot ${i === activeIndex ? 'dot-active' : ''}`}
                                onClick={() => goToIndex(i)}
                                aria-label={`Go to article ${i + 1}`}
                            />
                        ))}
                    </div>
                </div>

                {visibleCount < filteredBlogs.length && (
                    <div className="load-more-wrapper scroll-animate">
                        <button className="load-more-btn" onClick={handleLoadMore}>
                            Load More Articles
                            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M12 5v14M5 12l7 7 7-7"/>
                            </svg>
                        </button>
                    </div>
                )}
            </div>
        </section>
    );
};

export default BlogSection;
