import React from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/ArticlePage.css';
import blogData from '../shared/blogData';

const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    const options = { day: 'numeric', month: 'short', year: 'numeric' };
    return date.toLocaleDateString('en-US', options);
};

const ArticlePage = () => {
    const { id } = useParams();
    const blog = blogData.find(b => String(b.id) === String(id));

    if (!blog) {
        return (
            <div className="article-page">
                <div className="article-container">
                    <h2>Article not found</h2>
                    <p>The article you are looking for does not exist.</p>
                    <Link to="/">Return to Blog</Link>
                </div>
            </div>
        );
    }

    return (
        <div className="article-page">
            <div className="article-container">
                <Link className="back-link" to="/">← Back to Blog</Link>

                <div className="article-hero">
                    <img src={blog.image} alt={blog.title} />
                </div>

                <h1 className="article-title">{blog.title}</h1>
                <div className="article-meta">{blog.author} • {formatDate(blog.date)}</div>

                <div className="article-intro">{blog.excerpt}</div>

                <div className="article-body">
                    {/* We don't have the full HTML for each article locally. As a helpful fallback we
                        show the excerpt and then provide an embedded view of the original link. */}
                    <p>Read the full original article below. If embedding is blocked by the remote site,
                       click the link to open it in a new tab.</p>

                    <div className="article-embed">
                        <iframe title={blog.title} src={blog.link} sandbox="allow-same-origin allow-scripts allow-popups allow-forms" />
                    </div>

                    <p className="external-link">Or open full article: <a href={blog.link} target="_blank" rel="noreferrer">{blog.link}</a></p>
                </div>
            </div>
        </div>
    );
};

export default ArticlePage;
