import React, { useEffect, useState, useRef } from 'react';
import './HeroSection.css';

import slide0 from '../z.jpg';
import slide1 from '../z1.jpg';
import slide2 from '../z3.jpg';

const slides = [
    {
        img: slide0,
        title: 'Art is the language of the soul',
        caption: 'A canvas remembers every honest brush stroke — reflect, feel, and live with it.'
    },
    {
        img: slide1,
        title: 'Color shapes our memories',
        caption: 'Every hue is a thought — embrace the mood it creates.'
    },
    {
        img: slide2,
        title: 'Between silence and form',
        caption: 'True art finds the space where silence speaks loudest.'
    }
];

const HeroSection = () => {
    const [index, setIndex] = useState(0);
    const timerRef = useRef(null);

    useEffect(() => {
        // autoplay slides every 2 seconds
        timerRef.current = setInterval(() => {
            setIndex(prev => (prev + 1) % slides.length);
        }, 2000);

        return () => clearInterval(timerRef.current);
    }, []);

    const goTo = (i) => {
        setIndex(i % slides.length);
        // reset timer
        clearInterval(timerRef.current);
        timerRef.current = setInterval(() => setIndex(prev => (prev + 1) % slides.length), 2000);
    };

    return (
        <section className="hero">
            <div className="hero-slider">
                {slides.map((s, i) => (
                    <div
                        className={`hero-slide ${i === index ? 'active' : ''}`}
                        key={i}
                        aria-hidden={i !== index}
                    >
                        <img src={s.img} alt={s.title} />
                        <div className="hero-slide-copy">
                            <h2>{s.title}</h2>
                            <p>{s.caption}</p>
                        </div>
                    </div>
                ))}
            </div>

            <div className="hero-controls">
                {slides.map((_, i) => (
                    <button
                        key={i}
                        className={`dot ${i === index ? 'active' : ''}`}
                        onClick={() => goTo(i)}
                        aria-label={`Go to slide ${i + 1}`}
                    />
                ))}
            </div>
        </section>
    );
};

export default HeroSection;
