import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import HeroSection from './components/HeroSection';
import BlogSection from './components/BlogSection';
import Footer from './components/Footer';
import ArticlePage from './components/ArticlePage';
import './App.css';

function HomePage() {
    return (
        <>
            <HeroSection />
            <BlogSection />
        </>
    );
}

import { useEffect } from 'react';
import initVirtualScroll from './virtualScroll';

function App() {
    useEffect(() => {
        // Initialize virtual scrolling on the main content area so the native
        // browser scrollbar can be hidden while preserving scroll input.
        const cleanup = initVirtualScroll('main.site-main');
        return () => cleanup && cleanup();
    }, []);

    return (
        <Router>
            <div className="app">
                <Header />
                <main className="site-main">
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/article/:id" element={<ArticlePage />} />
                    </Routes>
                </main>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
