import React, { useEffect, useRef } from 'react';
import './HeroSection.css';
import heroBg from '../save.jpg';
import sideImg from '../2side.jpg';

const HeroSection = () => {
    const heroRef = useRef(null);

    useEffect(() => {
        const setHeaderHeightVar = () => {
            const header = document.querySelector('.header');
            const h = header ? header.offsetHeight : 80;
            document.documentElement.style.setProperty('--header-height', `${h}px`);
            // Also explicitly set the hero image max-height inline to avoid
            // timing/layout issues where CSS might be applied before the
            // header measurement is available. This prevents cropping when
            // returning to the hero after scrolling.
            try {
                const heroEl = heroRef.current;
                if (heroEl) {
                    const img = heroEl.querySelector('.hero-fullimg');
                    if (img) img.style.maxHeight = `calc(100vh - ${h}px)`;
                }
            } catch (e) {
                // ignore
            }
        };

        setHeaderHeightVar();
        window.addEventListener('resize', setHeaderHeightVar);

        // Ensure the hero image sizing is re-applied when the hero enters view
        const heroEl = heroRef.current;
        if (heroEl && 'IntersectionObserver' in window) {
            const io = new IntersectionObserver((entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) heroEl.classList.add('hero-in-view');
                    else heroEl.classList.remove('hero-in-view');
                });
            }, { threshold: 0.1 });
            io.observe(heroEl);
            return () => {
                io.disconnect();
                window.removeEventListener('resize', setHeaderHeightVar);
            };
        }

        return () => window.removeEventListener('resize', setHeaderHeightVar);
    }, []);

    return (
        <section className="hero" ref={heroRef} role="img" aria-label="Hero background image">
            {/* Left side decorative image */}
            <div className="hero-side hero-side-left">
                <img src={sideImg} alt="" />
            </div>

            <div className="hero-bg">
                <img className="hero-fullimg" src={heroBg} alt="Hero background" />
            </div>

            {/* Right side decorative image */}
            <div className="hero-side hero-side-right">
                <img src={sideImg} alt="" />
            </div>

            <div className="hero-copy">
                <div className="hero-copy-inner">
                    <h1>Art speaks where words fall short</h1>
                    <p>Original ideas, colour and form â€” a canvas holds memory and meaning.</p>
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
