import React from 'react';
import './BlogListItem.css';

const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    const options = { day: 'numeric', month: 'short', year: 'numeric' };
    return date.toLocaleDateString('en-US', options);
};

const BlogListItem = ({ blog, reverse }) => {
    return (
        <article className={`post-row ${reverse ? 'reverse' : ''}`}>
            <a href={blog.link} className="post-image" aria-label={blog.title}>
                <img src={blog.image} alt={blog.title} loading="lazy" />
            </a>

            <div className="post-content">
                <div className="post-meta">
                    <span className="post-author">{blog.author}</span>
                    <span className="meta-divider">â€¢</span>
                    <span className="post-date">{formatDate(blog.date)}</span>
                </div>

                <h3 className="post-title"><a href={blog.link}>{blog.title}</a></h3>
                <p className="post-excerpt">{blog.excerpt}</p>

                <a className="post-readmore" href={blog.link}>Read Article</a>
            </div>
        </article>
    );
};

export default BlogListItem;
