import { useEffect } from 'react';

// Lightweight scroll reveal hook using IntersectionObserver
// Observes elements with class `scroll-animate` or attribute `data-sr`
// Adds the `visible` class when element enters viewport. Honors
// optional `data-sr-delay` (seconds) and `data-sr-class` to apply variant classes.
export default function useScrollReveal({ root = null, margin = '0px 0px -10% 0px', threshold = 0.12 } = {}) {
  useEffect(() => {
    if (typeof window === 'undefined' || !('IntersectionObserver' in window)) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (!entry.isIntersecting) return;

          const el = entry.target;

          // honor delay attribute (in seconds)
          const delay = el.getAttribute('data-sr-delay');
          if (delay) el.style.transitionDelay = `${delay}s`;

          el.classList.add('visible');

          // If element requests a one-time reveal, unobserve it for performance
          observer.unobserve(el);
        });
      },
      { root, rootMargin: margin, threshold }
    );

    const elements = Array.from(document.querySelectorAll('.scroll-animate, [data-sr]'));
    elements.forEach((el) => observer.observe(el));

    return () => {
      elements.forEach((el) => observer.unobserve(el));
      observer.disconnect();
    };
  }, [root, margin, threshold]);
}
